import { ServiceScope } from '@microsoft/sp-core-library';
import { IWebPartHost, IWebPartData, WebPartConfigurationEvent } from '@microsoft/sp-webpart-base';
/**
 * CanvasWebPartHost is the IWebPartHost implementation used by the Canvas. Each Canvas
 * will instantiate their own CanvasWebPartHost, which means the web parts are scoped by
 * Canvas.
 */
export default class CanvasWebPartHost implements IWebPartHost {
    private _handleCanvasChanged;
    private _handleResize;
    private _reclaimFocus;
    private _serviceScope;
    private _setSelectedWebPartId;
    /**
     * @param handleCanvasChanged - Optional. Callback invoked when web parts are interacted with.
     */
    constructor(serviceScope: ServiceScope, handleResize: (element: HTMLElement, duration: number, margin?: number) => void, handleCanvasChanged?: () => void, reclaimFocus?: () => void, setSelectedWebPartId?: (id: string) => void);
    /**
     * @see IWebPartHost
     */
    readonly serviceScope: ServiceScope;
    /**
     * @see IWebPartHost
     */
    setDirty(wpInstanceId: string): void;
    /**
     * @see IWebPartHost
     */
    webPartConfigurationEventCallback(event: WebPartConfigurationEvent, data?: IWebPartData): void;
}
