export interface IToolbarButton {
    /**
     * If the button can have active state
     */
    canBeActive?: boolean;
    /**
     * Class name for the button element
     */
    className?: string;
    /**
     * The icon name in Office Fabric UI icons at http://dev.office.com/fabric/styles
     * Note: Do not include 'ms-icon--' prefix
     * For example, pass 'gear' if you want to use ms-icon--gear icon
     */
    fabricIconKey?: string;
    /**
     * Class name for the icon element
     * Ignored if fabricIconKey is provided
     */
    iconClassName?: string;
    /**
     * Keyboard shortcut e.g. 'Ctrl+B'
     */
    shortcut?: string;
    /**
     * Html title attribute of the button
     */
    title?: string;
    /**
     * Text label for the button, typically provided as a substitute for an icon
     */
    label?: string;
}
