import { IToolbarButton } from '../../sp-canvas/toolbar/toolbarButton/IToolbarButton';
export interface IRichTextFormat {
    /**
     * Format name in the editor
     */
    editorKey: string;
    /**
     * Only one block style can be applied to a paragraph e.g. heading, quote
     */
    isBlockStyle: boolean;
    /**
     * If the format applies to the whole line
     */
    isLine: boolean;
    /**
     * If editor natively understands this format (doesn't need registration)
     */
    isEditorNative: boolean;
    /**
     * Format name in SPRte
     */
    key: string;
    /**
     * Format name in HTML5 contenteditable commands, used for calling queryCommandState
     */
    browserKey?: string;
    /**
     * Value to set this format using the editor. Do NOT Specify for boolean formats
     * If not set, true/false boolean values are used for setting/unsetting the format
     * Example: For alignRight we need to pass {align:'right'} to Quill, so editorKey=align and editorValue=right
     * Example: For bold we need to pass {bold:true} to Quill, so editorKey=bold and editorValue should be undefined
     */
    editorValue?: string | number | boolean;
    /**
     * List of format names masked by this format
     * - a masked format cannot be set when any of its masking formats is active
     * - applying any of the masking formats will erase its masked formats
     */
    masks?: string[];
    /**
     * List of formats that mask this format
     * IMPORTANT: DO NOT set this attribute. It populated in the constructor based on 'masks' attribute
     */
    maskedBy?: string[];
    button?: IToolbarButton;
}
