import IDataError from './IDataError';
import IDataResponse from './IDataResponse';
import IDataRequest from './IDataRequest';
/**
 * Represents the interface for a data provider
 */
interface IDataProvider {
    /**
     * Success delegate
     */
    onDataSuccess: (response: IDataResponse) => void;
    /**
     * Error delegate
     */
    onDataError: (error: IDataError) => void;
    /**
     * Module to fetch data
     * @param request represents the input request
     * @param onSuccess represents the success delegate
     * @param onError represents the error delegate
     *
     * @returns the identifier associated with this request
     */
    getData(request: IDataRequest, onSuccess: (response: IDataResponse) => void, onError: (error: IDataError) => void): string;
}
export default IDataProvider;
