/**
 * Used to display a placeholder in case of no or temporary content. Button is optional.
 *
 * @public
 */
export interface IPlaceholderProps {
    /**
     * Text description for the placeholder. Appears bellow the Icon and IconText.
     */
    description: string;
    /**
     * Icon className from the MDL2 set. Example: 'ms-Icon--Add'.
     */
    icon: string;
    /**
     * Heading displayed against the Icon.
     */
    iconText: string;
    /**
     * Text label to be displayed on button below the description.
     * Optional: As the button is optional.
     */
    buttonLabel?: string;
    /**
     * onClick handler for the button.
     * Optional: As the button is optional.
     */
    onAdd?: () => void;
    /**
     * This className is applied to the root element of content. Use this to
     * apply custom styles to the placeholder.
     */
    contentClassName?: string;
}
/**
 * Interface for properties used to display the loading spinner in the web part display area.
 *
 * @public
 */
export interface IPlaceholderSpinnerProps {
    /**
     * Label associated with the spinner.
     */
    label: string;
    /**
     * Hide spinner at start if set to true.
     * In such case, use state showSpinner to true when to show the spinner
     */
    hideSpinner?: boolean;
}
