"use strict";
var React = require("react");
var chai_1 = require("chai");
var enzyme_1 = require("enzyme");
var Placeholder_1 = require("../../components/placeHolder/Placeholder");
describe('Placeholder component', function () {
    it('should render passed children', function () {
        var childNode = React.createElement('textarea', undefined, 'test');
        var placeholder = enzyme_1.shallow(React.createElement(Placeholder_1.default, {
            description: '',
            icon: '',
            iconText: '',
            children: childNode
        }));
        chai_1.expect(placeholder).not.to.be.null.and.not.undefined;
        chai_1.expect(placeholder.find('textarea')).to.have.lengthOf(1);
        chai_1.expect(placeholder.find('textarea').text()).to.equal('test');
    });
});
