"use strict";
var chai_1 = require("chai");
var React = require("react");
var enzyme_1 = require("enzyme");
var MobilePreview_1 = require("../../../mobilePreview/MobilePreview");
var MobilePreviewDeviceTypeSelector_1 = require("../MobilePreviewDeviceTypeSelector");
describe('Testing device type selector component', function () {
    var mobilePreview;
    var deviceSelector;
    beforeEach(function () {
        mobilePreview = enzyme_1.mount(React.createElement(MobilePreview_1.default, { deviceType: MobilePreview_1.DeviceType.Phone }));
        deviceSelector = mobilePreview.find(MobilePreviewDeviceTypeSelector_1.default);
    });
    afterEach(function () {
        mobilePreview.unmount();
    });
    it('changes from phone to tablet and from tablet to phone on click', function () {
        chai_1.assert.equal(mobilePreview.state('deviceType'), MobilePreview_1.DeviceType.Phone, 'device type equals phone');
        deviceSelector.find('a').last().simulate('click');
        chai_1.assert.equal(mobilePreview.state('deviceType'), MobilePreview_1.DeviceType.Tablet, 'device type equals tablet');
        deviceSelector.find('a').first().simulate('click');
        chai_1.assert.equal(mobilePreview.state('deviceType'), MobilePreview_1.DeviceType.Phone, 'device type equals phone');
    });
});
