"use strict";
var chai_1 = require("chai");
var React = require("react");
var enzyme_1 = require("enzyme");
var office_ui_fabric_react_1 = require("office-ui-fabric-react");
var MobilePreview_1 = require("../../../mobilePreview/MobilePreview");
var MobilePreviewURLEntry_1 = require("../MobilePreviewURLEntry");
var MobilePreviewHeaderBar_1 = require("../../MobilePreviewHeaderBar");
describe('Testing URL entry component', function () {
    var mobilePreview;
    var urlEntry;
    beforeEach(function () {
        mobilePreview = enzyme_1.mount(React.createElement(MobilePreview_1.default, { deviceType: MobilePreview_1.DeviceType.Phone }));
        urlEntry = mobilePreview.find(MobilePreviewHeaderBar_1.default).find(MobilePreviewURLEntry_1.default);
    });
    afterEach(function () {
        mobilePreview.unmount();
    });
    it('changes the URL to users entry', function () {
        var newURL = 'www.bing.com';
        urlEntry.find('input').simulate('change', { target: { value: newURL } });
        urlEntry.find('button').simulate('click');
    });
    it('url doesn\'t change when user clicks submit with an empty url', function () {
        var url = mobilePreview.state('url');
        urlEntry.find(office_ui_fabric_react_1.Button).simulate('click');
        chai_1.assert.equal(mobilePreview.state('url'), url);
    });
});
