"use strict";
var chai_1 = require("chai");
var React = require("react");
var enzyme_1 = require("enzyme");
var MobilePreview_1 = require("../../mobilePreview/MobilePreview");
var MobilePreviewDeviceTypeSelector_1 = require("../mobilePreviewDeviceTypeSelector/MobilePreviewDeviceTypeSelector");
var MobilePreviewHeaderBar_1 = require("../../mobilePreviewHeaderBar/MobilePreviewHeaderBar");
var MobilePreviewURLEntry_1 = require("../mobilePreviewURLEntry/MobilePreviewURLEntry");
describe('Testing MobilePreview header bar', function () {
    var mobilePreview;
    var headerBar;
    beforeEach(function () {
        mobilePreview = enzyme_1.mount(React.createElement(MobilePreview_1.default, { deviceType: MobilePreview_1.DeviceType.Phone }));
        headerBar = mobilePreview.find(MobilePreviewHeaderBar_1.default);
    });
    afterEach(function () {
        mobilePreview.unmount();
    });
    it('renders all the components', function () {
        chai_1.assert.equal(headerBar.find(MobilePreviewURLEntry_1.default).length, 1, 'contains URL entry bar');
        chai_1.assert.equal(headerBar.find(MobilePreviewDeviceTypeSelector_1.default).length, 1, 'contains URL entry bar');
    });
});
