"use strict";
var chai_1 = require("chai");
var React = require("react");
var enzyme_1 = require("enzyme");
var MobilePreview_1 = require("../../mobilePreview/MobilePreview");
var MobilePreviewRotate_1 = require("../../mobilePreviewRotate/MobilePreviewRotate");
describe('Testing rotate feature', function () {
    var mobilePreviewRotate;
    var mobilePreview;
    beforeEach(function () {
        mobilePreview = enzyme_1.mount(React.createElement(MobilePreview_1.default));
        mobilePreviewRotate = mobilePreview.find(MobilePreviewRotate_1.default);
    });
    afterEach(function () {
        mobilePreview.unmount();
    });
    it('changes from portrait to landscape on click', function () {
        chai_1.assert.equal(mobilePreviewRotate.prop('currentOrientation'), MobilePreview_1.DeviceOrientation.Portrait, 'current orientation should start off as Portrait');
        mobilePreviewRotate.find('a').simulate('click');
        chai_1.assert.equal(mobilePreviewRotate.prop('currentOrientation'), MobilePreview_1.DeviceOrientation.Landscape, 'current orientation should now be Landscape');
    });
});
